import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { TlvService } from '../services/tlv.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit {
  email = new FormControl('',
    Validators.compose([
      Validators.required,
      Validators.email
    ]))


  constructor(private fb: FormBuilder,
    private tlvService: TlvService) { }


  ngOnInit(): void {
  }

  sendEmail() {

    this.tlvService.sendEmail(this.email.value || '').subscribe((res: string) => {

      Swal.fire('', 'Success connect to API ' + res, 'success');


    },
      () => {

        Swal.fire('', "Failed to connect API", 'error');


      });
  }


}
